import React, { Component } from "react";
import Button from "react-bootstrap/es/Button";
import { withRouter } from "react-router-dom";
import axios from "axios";

export class Paste extends Component {
  constructor(props) {
    super(props);
    this.state = {
      spd_paste: "",
      spd_array: [],
      loading: false,
      data: []
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleSPDChange = this.handleSPDChange.bind(this);
  }

  handleSPDChange = event => {
    this.setState({ spd_paste: event.target.value }, function() {
      console.log("setState completed", this.state);
    });
  };
  handleSubmit(event) {
    this.setState({ spd_array: this.state.spd_paste.split("\n") }, function() {
      console.log("setState completed", this.state);
      //event.preventDefault();
      this.setState({ loading: true }, () => {
        axios.defaults.headers.common = {};
        axios.defaults.headers.common.accept = "application/json";
        axios
          .post("http://dtn3.pnl.gov:8080/api/tm30Paste", {
            //.post("http://localhost:5000/api/tm30Paste", {
            info: this.state.spd_array
          })
          .then(res => {
            return res.data;
          })
          .then(data => {
            //console.log(data.data);
            this.setState({
              loading: false,
              data: data.data,
              tableReady: true
            });
            /**
             * Not Really Used anymore but still could be used later
             */
            //localStorage.setItem("data", JSON.stringify(this.state.data));
            /**
             * Pushes the data to the Reports page and redirects to that page
             */
            this.props.history.push({
              pathname: "/Report",
              state: {
                data: this.state.data
              }
            });
          });
      });
    });
  }
  render() {
    return (
      <div>
        <h2>Option C</h2>
        <form>
          <div style={{ position: "inherit", display: "inline-block" }}>
            <label>
              Start Wavelength (nm):
              <br />
              <input></input>
            </label>
            <br />
            <label>
              End Wavelength (nm):
              <br />
              <input></input>
            </label>
            <br />
            <label>
              Interval (nm):
              <br />
              <input></input>
            </label>
          </div>
          <div
            style={{
              position: "inherit",
              display: "inline-block",
              width: "5%"
            }}
          ></div>
          <div style={{ position: "inherit", display: "inline-block" }}>
            <h3>Paste in your SPD</h3>
            <textarea
              rows="5"
              cols="50"
              onChange={this.handleSPDChange}
            ></textarea>
          </div>
          <br />
          <Button bsSize="sm" onClick={this.handleSubmit}>
            Calculate
          </Button>
        </form>
      </div>
    );
  }
}

export default withRouter(Paste);
