import React, { Component } from "react";
import Plot from "react-plotly.js";

export class CVGPlot extends Component {
  constructor(props) {
    super(props);
    this.xtest = this.props.xtest;
    this.xtest.push(this.xtest[0]);
    this.ytest = this.props.ytest;
    this.ytest.push(this.ytest[0]);
    console.log(this.xtest);
    console.log(Math.max(...this.xtest));
    console.log(Math.min(...this.xtest));
    var i;
  }
  render() {
    var xmax = Math.max(...this.xtest);
    var xmin = Math.min(...this.xtest);
    //var xstd = Math.std(...this.xtest);
    const n = this.xtest.length;
    const yxmean = this.xtest.reduce((a, b) => a + b) / n;
    const ymean = this.ytest.reduce((a, b) => a + b) / n;
    //const stdDevX = Math.sqrt(
    //  this.xtest.map(x => Math.pow(x - mean, 2)).reduce((a, b) => a + b) / n
    //);
    console.log(stdDevX);
    var ymax = Math.max(this.ytest);
    var ymin = Math.min(this.ytest);
    console.log(`xmax = ${xmax}`);
    console.log(`xmin = ${xmin}`);
    return (
      <div>
        <Plot
          data={[
            {
              name: "Test",
              x: this.xtest,
              y: this.ytest,
              type: "scatter",
              line: { shape: "spline", smoothing: 1.3 },
              mode: "lines+points",
              connectgaps: true,
              marker: { color: "red" }
            },
            {
              /*
              name: "Reference",
              x: this.wavelength,
              y: this.spd_ref,
              type: "scatter",
              mode: "lines+points",
              marker: { color: "black" }
              */
            }
          ]}
          layout={{
            width: 500,
            height: 500,
            margin: {
              l: 60,
              r: 10,
              b: 45,
              t: 10,
              pad: 4
            },
            xaxis: {
              range: [xmin + -2 * xmean, xmax + 2 * xmean],
              title: {
                text: ""
              },
              showticklabels: false
            },
            yaxis: {
              range: [ymin + -2 * ymean, ymax + 2 * ymean],
              title: {
                text: ""
              },
              tickformat: "",
              showticklabels: false
            }
          }}
          config={{ displayModeBar: false }}
        />
      </div>
    );
  }
}

export default CVGPlot;
