import React from 'react';
import Button from 'react-bootstrap/es/Button';
import { render } from 'react-dom';
import axios from 'axios';
import LoadingSpinner from 'pnnl-react-core/lib/Loader';

class FileInput extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.fileInput = React.createRef();
    this.formData = new FormData();
    this.state = {
      loading: false,
      data: [],
      tableReady: false
    }
  }
  handleSubmit(event) {
    //console.log(event.target.files[0]);
    event.preventDefault();
    //Send file to server
    this.setState({ loading: true }, () => {
      this.formData.append('file', this.fileInput.current.files[0]);
      axios.post('http://localhost:5000/api/uploadSPDX', this.formData)
        .then(res => {
          return res.data;
        })
        .then(data => {
          console.log(data.data);
          this.setState({
            loading: false,
            data: data.data,
            tableReady: true
          });
        })
    });
  }

  render() {
    var columns = ['Wavelength', 'Power Value'];
    const loading = this.state.loading;
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Import .spdx file:
            <input type="file" ref={this.fileInput} />
        </label>
        <br />
        {loading ? <div><label> Calculating Results <br /> <LoadingSpinner color="copper" size="6" /> </label>
        </div>
          : <Button bsSize="sm" type="submit">Calculate</Button>}

        {this.state.tableReady ? <div>
          <table striped bordered hover variant="dark">
            <thead>
              <tr>
                <th>Wavelength</th>
                <th>Power Value</th>
              </tr>
            </thead>
            <tbody>
              {this.state.data.map(function (item, key) {
                return (
                  <tr key={key}>
                    <td>{item.wavelength}</td>
                    <td>{item.powerValue}</td>
                  </tr>
                )
              })}
            </tbody>
          </table>
        </div>
          : <div></div>}
      </form>

    );
  }
}
export default FileInput;




