import React, { Component } from "react";
import Table from "react-bootstrap/es/Table";
import Grid from "react-bootstrap/es/Grid";
import Row from "react-bootstrap/es/Row";
import Col from "react-bootstrap/es/Col";

export class Numerical extends Component {
  constructor(props) {
    super(props);
    this.tm30 = this.props.tm30;

    this.lcs = [];
    this.lhs = [];
    this.lcf = [];
    this.lcs_lhs_lcf = [];
    this.sample_count = [];
    this.atest = [];
    this.btest = [];

    this.colors = [
      "#b45c60",
      "#cb7761",
      "#cb824b",
      "#d7ad67",
      "#ac9a5d",
      "#919E60",
      "#678B5F",
      "#62B08F",
      "#7CBABA",
      "#2C797D",
      "#54778C",
      "#7189B1",
      "#988CA9",
      "#735876",
      "#8F6682",
      "#B97B8E"
    ];

    var i;
    for (i = 0; i < 16; i++) {
      this.lcs.push(Math.round(this.tm30.local_chroma_shift[i]));
      this.lhs.push(Math.round(this.tm30.local_hue_shift[i] * 100) / 100);
      this.lcf.push(Math.round(this.tm30.local_color_fidelity[i]));
      this.sample_count.push(this.tm30.sample_count[i]);
      this.atest.push(Math.round(this.tm30.a_prime_test_j[i] * 100) / 100);
      this.btest.push(Math.round(this.tm30.b_prime_test_j[i] * 100) / 100);

      this.lcs_lhs_lcf.push(
        <tr>
          <td style={{ "background-color": this.colors[i] }}>
            <b>{i + 1}</b>
          </td>
          <td>{this.lcs[i]}%</td>
          <td>{this.lhs[i]}</td>
          <td>{this.lcf[i]}</td>
          <td>{this.sample_count[i]}</td>
          <td>{this.atest[i]}</td>
          <td>{this.btest[i]}</td>
        </tr>
      );
    }
  }
  render() {
    return (
      <div>
        <Grid>
          <h2 align="center">Color Rendition Global Averages </h2>
          <Row>
            <Col xs={2}></Col>
            <Col xs={8}>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>
                      Fidelity Index <hr /> R<sub>f</sub>
                    </th>
                    <th>
                      Gamut Index <hr /> R<sub>g</sub>
                    </th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>{Math.round(this.tm30.rf)}</td>
                    <td>{Math.round(this.tm30.rg)}</td>
                  </tr>
                </tbody>
              </Table>
            </Col>
            <Col xs={2}></Col>
          </Row>
          <Row>
            <h2 align="center">Color Rendition Local Averages </h2>
            <Table striped bordered hover>
              <thead>
                <tr>
                  <th>
                    Hue-Angle Bin <hr /> <i>j</i>
                  </th>
                  <th align="center">
                    Local Chroma Shift <hr /> R
                    <sub>
                      cs,h<i>j</i>
                    </sub>
                  </th>
                  <th>
                    Local Hue Shift <hr /> R
                    <sub>
                      hs,h<i>j</i>
                    </sub>
                  </th>
                  <th>
                    Local Color Fidelity <hr /> R
                    <sub>
                      f,h<i>j</i>
                    </sub>
                  </th>
                  <th>
                    Sample Count <hr /> <i>m</i>
                  </th>
                  <th colSpan="2">
                    Test Coordinates <hr />{" "}
                    <i>
                      a'
                      <sub>
                        test,j <div class="tab" />
                      </sub>
                    </i>{" "}
                    <i>
                      b'<sub>test,j</sub>
                    </i>
                  </th>
                </tr>
              </thead>
              <tbody>
                {this.lcs_lhs_lcf[0]}
                {this.lcs_lhs_lcf[1]}
                {this.lcs_lhs_lcf[2]}
                {this.lcs_lhs_lcf[3]}
                {this.lcs_lhs_lcf[4]}
                {this.lcs_lhs_lcf[5]}
                {this.lcs_lhs_lcf[6]}
                {this.lcs_lhs_lcf[7]}
                {this.lcs_lhs_lcf[8]}
                {this.lcs_lhs_lcf[9]}
                {this.lcs_lhs_lcf[10]}
                {this.lcs_lhs_lcf[11]}
                {this.lcs_lhs_lcf[12]}
                {this.lcs_lhs_lcf[13]}
                {this.lcs_lhs_lcf[14]}
                {this.lcs_lhs_lcf[15]}
              </tbody>
            </Table>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Numerical;
