import React, { Component } from "react";
import Plot from "react-plotly.js";

export class ELFPlot extends Component {
  constructor(props) {
    super(props);
    this.spd_test = this.props.test;
    this.spd_ref = this.props.ref_spd;
    var i;

    this.wavelength = [];
    for (i = 0; i < 400; i++) {
      this.wavelength[i] = i + 380;
    }
  }
  render() {
    return (
      <Plot
        data={[
          {
            name: "Test",
            x: this.wavelength,
            y: this.spd_test,
            type: "scatter",
            mode: "lines+points",
            marker: { color: "red" }
          },
          {
            name: "Reference",
            x: this.wavelength,
            y: this.spd_ref,
            type: "scatter",
            mode: "lines+points",
            marker: { color: "black" }
          }
        ]}
        layout={{
          width: 500,
          height: 400,
          margin: {
            l: 60,
            r: 0,
            b: 45,
            t: 10,
            pad: 4
          },
          xaxis: {
            title: {
              text: "Wavelength (nm)"
            }
          },
          yaxis: {
            title: {
              text: "Radiant Power (Equal Luminous FLux)"
            },
            tickformat: "",
            showticklabels: false
          }
        }}
        config={{ displayModeBar: false }}
      />
    );
  }
}

export default ELFPlot;
