import React, { Component } from "react";
import Plot from "react-plotly.js";

export class CVGPlot extends Component {
  constructor(props) {
    super(props);
    this.xtest = this.props.xtest;
    this.xtest.push(this.xtest[0]);
    this.ytest = this.props.ytest;
    this.ytest.push(this.ytest[0]);

    this.xref = this.props.xref;
    this.xref.push(this.xref[0]);
    this.yref = this.props.yref;
    this.yref.push(this.yref[0]);

    this.rf = Math.round(this.props.rf);
    this.rg = Math.round(this.props.rg);
    this.cct = Math.round(this.props.cct);
    this.duv = Math.round(this.props.duv * 10000) / 10000;

    this.bin_lines = [
      {
        x: [-1.5, 1.5],
        y: [0, 0],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [-1.39, 1.39],
        y: [-0.57, 0.57],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [-1.06, 1.06],
        y: [-1.06, 1.06],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [-0.57, 0.57],
        y: [-1.39, 1.39],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [0, 0],
        y: [-1.5, 1.5],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [0.57, -0.57],
        y: [-1.39, 1.39],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [1.06, -1.06],
        y: [-1.06, 1.06],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [1.39, -1.39],
        y: [-0.57, 0.57],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "dash",
          width: 2
        },
        marker: { color: "grey" },
        showlegend: false
      }
    ];

    const colors = [
      "#b45c60",
      "#cb7761",
      "#cb824b",
      "#d7ad67",
      "#ac9a5d",
      "#919E60",
      "#678B5F",
      "#62B08F",
      "#7CBABA",
      "#2C797D",
      "#54778C",
      "#7189B1",
      "#988CA9",
      "#735876",
      "#8F6682",
      "#B97B8E"
    ];

    var i;
    this.arrows = [];
    var i_arrow;
    for (i = 0; i < 16; i++) {
      i_arrow = {
        x: this.xtest[i],
        y: this.ytest[i],
        xref: "x",
        yref: "y",
        text: "",
        showarrow: true,
        arrowhead: 2,
        axref: "x",
        ayref: "y",
        ax: this.xref[i],
        ay: this.yref[i],
        arrowcolor: colors[i]
      };
      this.arrows.push(i_arrow);
    }
    console.log(this.arrows);
  }
  render() {
    return (
      <Plot
        data={[
          {
            name: "Test",
            x: this.xtest,
            y: this.ytest,
            type: "scatter",
            line: { shape: "spline", smoothing: 1.3 },
            mode: "lines+points",
            connectgaps: true,
            marker: { color: "red" }
          },
          {
            name: "Reference",
            x: this.xref,
            y: this.yref,
            type: "scatter",
            line: { shape: "spline", smoothing: 1.3 },
            mode: "lines+points",
            connectgaps: true,
            marker: { color: "black" }
          }
          //...this.bin_lines
        ]}
        layout={{
          autosize: true,
          width: 700,
          height: 600,

          margin: {
            t: 40,
            l: 40,
            b: 40
          },

          xaxis: {
            visible: false,
            range: [-1.5, 1.5],
            title: {
              text: ""
            }
            //showticklabels: false
          },
          yaxis: {
            visible: false,
            range: [-1.5, 1.5],
            title: {
              text: ""
            },
            tickformat: ""
            //showticklabels: false
          },
          images: [
            {
              //source: "http://localhost:8000/CVGbackground.png",
              source: "http://localhost:8000/CVG_bin_lines.png",
              xref: "x",
              yref: "y",
              x: -1.5,
              y: 1.5,
              sizex: 3,
              sizey: 3,
              sizing: "stretch",
              layer: "below"
            }
          ],
          annotations: [
            ...this.arrows,
            {
              x: -1.25,
              y: 1.25,
              xref: "x",
              yref: "y",
              text: `<b>${this.rf}</b><br /><i>Rf</i>`,
              font: {
                size: 20
              },
              ax: 0,
              ay: 0
            },
            {
              x: 1.25,
              y: 1.25,
              xref: "x",
              yref: "y",
              text: `<b>${this.rg}</b><br /><i>Rg</i>`,
              font: {
                size: 20
              },
              ax: 0,
              ay: 0
            },
            {
              x: 1.15,
              y: -1.25,
              xref: "x",
              yref: "y",
              text: `<b>${this.duv}</b><br /><i>Duv</i>`,
              font: {
                size: 20
              },
              ax: 0,
              ay: 0
            },
            {
              x: -1.25,
              y: -1.25,
              xref: "x",
              yref: "y",
              text: `<b>   ${this.cct} K</b><br /><i>CCT</i>`,
              font: {
                size: 20
              },
              ax: 0,
              ay: 0
            }
          ]
        }}
        config={{ displayModeBar: false }}
      />
    );
  }
}

export default CVGPlot;
