import React, { Component } from "react";
import Plot from "react-plotly.js";

export class CVGPlot extends Component {
  constructor(props) {
    super(props);
    this.xtest = this.props.xtest;
    this.xtest.push(this.xtest[0]);
    this.ytest = this.props.ytest;
    this.ytest.push(this.ytest[0]);

    this.xref = this.props.xref;
    this.xref.push(this.xref[0]);
    this.yref = this.props.yref;
    this.yref.push(this.yref[0]);
    //console.log(this.xref);
    //console.log(this.yref);

    //image_filename = 'my-image.png'
    //encoded_image = base64.b64encode(open(image_filename, 'rb').read())

    const colors = [
      "#b45c60",
      "#cb7761",
      "#cb824b",
      "#d7ad67",
      "#ac9a5d",
      "#919E60",
      "#678B5F",
      "#62B08F",
      "#7CBABA",
      "#2C797D",
      "#54778C",
      "#7189B1",
      "#988CA9",
      "#735876",
      "#8F6682",
      "#B97B8E"
    ];

    var i;
    this.arrows = [];
    var i_arrow;
    for (i = 0; i < 16; i++) {
      i_arrow = {
        x: this.xtest[i],
        y: this.ytest[i],
        xref: "x",
        yref: "y",
        text: "",
        showarrow: true,
        arrowhead: 2,
        axref: "x",
        ayref: "y",
        ax: this.xref[i],
        ay: this.yref[i],
        arrowcolor: colors[i]
      };
      this.arrows.push(i_arrow);
    }
    console.log(this.arrows);
  }
  render() {
    return (
      <div>
        <Plot
          data={[
            {
              name: "Test",
              x: this.xtest,
              y: this.ytest,
              type: "scatter",
              line: { shape: "spline", smoothing: 1.3 },
              mode: "lines+points",
              connectgaps: true,
              marker: { color: "red" }
            },
            {
              name: "Reference",
              x: this.xref,
              y: this.yref,
              type: "scatter",
              line: { shape: "spline", smoothing: 1.3 },
              mode: "lines+points",
              connectgaps: true,
              marker: { color: "black" }
            }
          ]}
          layout={{
            width: 600,
            height: 600,
            margin: {
              l: 60,
              r: 10,
              b: 45,
              t: 10,
              pad: 4
            },
            xaxis: {
              //visible: false,
              range: [-1.5, 1.5],
              title: {
                text: ""
              }
              //showticklabels: false
            },
            yaxis: {
              //visible: false,
              range: [-2, 2],
              title: {
                text: ""
              },
              tickformat: ""
              //showticklabels: false
            },
            images: [
              {
                source: "http://localhost:8000/CVGbackground.png",
                xref: "x",
                yref: "y",
                x: -1.5,
                y: 1.5,
                sizex: 3,
                sizey: 6,
                sizing: "contain",
                layer: "below"
              }
            ],
            annotations: [
              ...this.arrows,
              {
                x: -1,
                y: 1.25,
                xref: "x",
                yref: "y",
                text: "Rt"
              }
            ]
          }}
          config={{ displayModeBar: false }}
        />
      </div>
    );
  }
}

export default CVGPlot;
