          width: 1
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [0, 0],
        y: [-1.5, 1.5],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "5px",
          width: 1
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [0.57, -0.57],
        y: [-1.39, 1.39],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "5px",
          width: 1
        },
        marker: { color: "grey" },
        showlegend: false
