          width: 1
        },
        marker: { color: "grey" },
        showlegend: false
      },
      {
        x: [1.39, -1.39],
        y: [-0.57, 0.57],
        mode: "lines",
        name: "Solid",
        line: {
          dash: "5px",
          width: 1
        },
        marker: { color: "grey" },
        showlegend: false
      }
    ];

    const colors = [
      "#b45c60",
      "#cb7761",
      "#cb824b",
      "#d7ad67",
      "#ac9a5d",
      "#919E60",
      "#678B5F",
      "#62B08F",
      "#7CBABA",
      "#2C797D",
      "#54778C",
      "#7189B1",
      "#988CA9",
      "#735876",
