      "#B97B8E"
    ];

    var i;
    this.arrows = [];
    var i_arrow;
    for (i = 0; i < 16; i++) {
      i_arrow = {
        x: this.xtest[i],
        y: this.ytest[i],
        xref: "x",
        yref: "y",
        text: "",
        showarrow: true,
        arrowhead: 2,
        axref: "x",
        ayref: "y",
        ax: this.xref[i],
        ay: this.yref[i],
        arrowcolor: colors[i]
      };
      this.arrows.push(i_arrow);
    }
  }
  render() {
    return (
      <Plot
        data={[
          ...this.bin_lines,
          {
            x: [0],
            y: [0],
            type: "scatter",
            mode: "markers+lines",
            marker: {
              size: 22,
              width: 50,
              color: "white",
              symbol: "circle",
              opacity: 0.8
            }
          },
          {
            x: [0],
