              color: "grey",
              symbol: "cross-thin-open"
            }
          },
          {
            name: "-20%",
            x: this.minus_20p_x,
            y: this.minus_20p_y,
            type: "scatter",
            line: { shape: "spline", smoothing: 1.3 },
            mode: "lines+points",
            connectgaps: true,
            marker: { color: "white" }
          },
          {
            name: "-10%",
            x: this.minus_10p_x,
