import React from 'react';
import Button from 'react-bootstrap/es/Button';
import { render } from 'react-dom';
import axios from 'axios';
import LoadingSpinner from 'pnnl-react-core/lib/Loader';

class FileInput extends React.Component {
    constructor(props) {
      super(props);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.fileInput = React.createRef();
      this.formData = new FormData();
      this.state = {
        loading: false
      }
    }
    handleSubmit(event) {
      //console.log(event.target.files[0]);
      event.preventDefault();
      //Send file to server
      this.setState({loading: true}, () => {
      this.formData.append('file',this.fileInput.current.files[0]);
      axios.post('http://localhost:5000/api/uploadSPDX', this.formData)
        .then(res => {
          console.log(res.body);
          this.setState({
            loading: false
          });
          alert(
            `Results ${
              res.data
            }`
          );
        })
      });
    }

    render() {
      const loading = this.state.loading;
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Import .spdx file:
            <input type="file" ref={this.fileInput} />
          </label>
          <br />
          {loading ? <label> Calculating Results <br/> <LoadingSpinner color="copper" size="6" /> </label> : <Button bsSize="sm" type="submit">Calculate</Button>}
        </form>
      );
    }
  }
  export default FileInput;
