    this.even_y = [];
    this.plus_10p_x = []; // +10% Ref Circle
    this.plus_10p_y = [];
    this.plus_20p_x = []; // +20% Ref Circle
    this.plus_20p_y = [];
    this.bin_lines = [];

    this.createBinLines();
    this.createBinNumbers();
    this.createChromaIsolines();

    /**
     * An array of the vector colors
     */
    const colors = [
      "#e32f32",
      "#e54f4f",
      "#fa843b",
      "#feb73c",
      "#cbcb50",
      "#7fb951",
      "#44c00b",
