      "#6a4e84", //14
      "#9d69a0", //15
      "#a65081" //16
    ];
    /**
     * Setting up the vector arrays
     */
    this.arrows = [];
    var i_arrow;
    var i;
    for (i = 0; i < 16; i++) {
      i_arrow = {
        x: this.xtest[i],
        y: this.ytest[i],
        xref: "x",
        yref: "y",
        text: "",
        showarrow: true,
        arrowhead: 2,
        arrowwidth: 3.5,
        axref: "x",
        ayref: "y",
        ax: this.xref[i],
        ay: this.yref[i],
        arrowcolor: colors[i]
      };
      this.arrows.push(i_arrow);
    }
  }
