/*
    Grunt file to build the static CSS/JS files for pnnl-app
    Author: Geoff Elliott
*/

module.exports = function(grunt) {
    'use strict';

    // Project configuration.
    grunt.initConfig({

        // Metadata.
        pkg: grunt.file.readJSON('package.json'),
        banner: '/*!\n' +
                ' * <%= pkg.displayName %> v<%= pkg.version %>\n' +
                ' * Build: <%= grunt.template.today %>\n' +
                ' * Author: <%= pkg.author %>\n' +
                ' */\n',

        // Task configuration.
        clean: {
            dist: 'dist'
        },

        copy: {
            fonts: {
                expand: true,
                src: 'src/fonts/*',
                filter: 'isFile',
                flatten: true,
                dest: 'dist/fonts'
            }
        },

        less: {
            dist: {
                options: {
                    sourceMap: true,
                    outputSourceFiles: true,
                    sourceMapURL: '<%= pkg.name %>.css.map',
                    sourceMapFilename: 'dist/css/<%= pkg.name %>.css.map'
                },
                files: {
                    'dist/css/<%= pkg.name %>.css': 'src/less/pnnl-icon.less'
                }
            },
            test: {
                options: {
                    sourceMap: false
                },
                files: {
                    'dist/css/demo.css': 'src/less/demo.less'
                }
            }
        },

        cssmin: {
            minify: {
                options: {
                    keepSpecialComments: '0',
                    advanced: false
                },
                src: 'dist/css/<%= pkg.name %>.css',
                dest: 'dist/css/<%= pkg.name %>.min.css'
            }
        },

        jade: {
            test: {
                options: {
                    pretty: true
                },
                files: {
                    'dist/html/<%= pkg.name %>.html': 'src/templates/index.jade'
                }
            }
        }
    })

    grunt.loadNpmTasks('grunt-contrib-clean')
    grunt.loadNpmTasks('grunt-contrib-copy')
    grunt.loadNpmTasks('grunt-contrib-less')
    grunt.loadNpmTasks('grunt-contrib-cssmin')
    grunt.loadNpmTasks('grunt-contrib-jade')

    grunt.registerTask('default', ['clean:dist', 'copy:fonts', 'less:dist', 'cssmin:minify', 'less:test', 'jade:test'])

}