/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line

import * as C from '../index';

storiesOf('Avatar', module)
  .add('image', () => <C.Avatar src="https://api.pnl.gov/operations/v2/portraits/self?type=image" />)
  .add('small sized image', () => <C.Avatar src="https://api.pnl.gov/operations/v2/portraits/self?type=image" size="sm" />)
  .add('extra small sized image', () => <C.Avatar src="https://api.pnl.gov/operations/v2/portraits/self?type=image" size="xs" />)
  .add('large sized image', () => <C.Avatar src="https://api.pnl.gov/operations/v2/portraits/self?type=image" size="lg" />)
  .add('icon', () => <C.Avatar><C.Icon icon="person" /></C.Avatar>)
  .add('customized icon', () => <C.Avatar><C.Icon icon="person" iconDisplay="outline" badge="add" /></C.Avatar>)
  .add('small icon', () => <C.Avatar size="sm"><C.Icon icon="person" /></C.Avatar>)
  .add('extra small icon', () => <C.Avatar size="xs"><C.Icon icon="person" /></C.Avatar>)
  .add('large icon', () => <C.Avatar size="lg"><C.Icon icon="person" /></C.Avatar>)
  .add('icon with background color', () => <C.Avatar className="danger"><C.Icon icon="trash" /></C.Avatar>)
  .add('icon with classNames', () => <C.Avatar className="silver text-gold"><C.Icon icon="folder" /></C.Avatar>)
  .add('text', () => <C.Avatar>JB</C.Avatar>)
  .add('small text', () => <C.Avatar size="sm">JB</C.Avatar>)
  .add('extra small text', () => <C.Avatar size="xs">JB</C.Avatar>)
  .add('large text', () => <C.Avatar size="lg">JB</C.Avatar>);
