/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import Chip from '../Chip';
import Avatar from '../Avatar';

import * as C from '../index';

storiesOf('AvatarInfo', module)
  .add('default', () => (
    <C.AvatarInfo />
  ))
  .add('left aligned', () => (
    <C.AvatarInfo title="Geoff Elliott" subTitle="Engineer" />
  ))
  .add('right aligned', () => (
    <C.AvatarInfo title="Geoff Elliott" subTitle="Engineer" align="right" />
  ))
  .add('center aligned', () => (
    <C.AvatarInfo title="Geoff Elliott" subTitle="Engineer" align="center" />
  ))
  .add('center aligned, no subTitle', () => (
    <C.AvatarInfo
      title="Geoff Elliott"
      align="center"
    />
  ))
  .add('left aligned, with element and text', () => (
    <C.AvatarInfo
      title="Geoff Elliott"
      subTitle={<span><Chip label="Active" color="emerald" size="xs" key="123" /> Engineer</span>}
      align="left"
    />
  ))
  .add('right aligned, with element and text', () => (
    <C.AvatarInfo
      title="Geoff Elliott"
      subTitle={<span>Engineer <Chip label="Active" color="emerald" size="xs" key="123" /></span>}
      align="right"
    />
  ))
  .add('center aligned, with element', () => (
    <C.AvatarInfo
      title="Geoff Elliott"
      subTitle={<Chip label="Active" color="emerald" size="xs" />}
      align="center"
    />
  ))
  .add('centered, with title as element', () => (
    <C.AvatarInfo
      title={<Chip label="Active" color="emerald" size="xs" />}
      subTitle="Geoff Elliott"
      avatarComponent={<Avatar src="https://api.pnl.gov/operations/v2/portraits/999999?type=image" />}
      align="center"
    />
  ))
  .add('right aligned, with title as element', () => (
    <C.AvatarInfo
      title={<Chip label="Active" color="emerald" size="xs" />}
      subTitle="Geoff Elliott"
      avatarComponent={<Avatar src="https://api.pnl.gov/operations/v2/portraits/999999?type=image" />}
      align="right"
    />
  ))
  .add('left aligned, with title as element', () => (
    <C.AvatarInfo
      title={<span>Geoff Elliott <span className="text-copper"><C.Icon icon="tools" /></span></span>}
      subTitle="Engineer"
      avatarComponent={<Avatar src="https://api.pnl.gov/operations/v2/portraits/999999?type=image" />}
    />
  ))
  .add('left aligned, with subTitle element', () => (
    <C.AvatarInfo
      subTitle={<Chip label="Active" color="emerald" size="xs" />}
      title="Geoff Elliott"
    />
  ))
  .add('right aligned, with subTitle element', () => (
    <C.AvatarInfo
      subTitle={<Chip label="Active" color="emerald" size="xs" />}
      title="Geoff Elliott"
      align="right"
    />
  ))
  .add('subTitle cannot be an object', () => (
    <C.AvatarInfo
      subTitle={{ name: 'hi' }}
      title="Geoff Elliott"
      align="right"
    />
  ))
  .add('array valid element', () => (
    <C.AvatarInfo
      subTitle={['Geoff', 'Elliott', 12345]}
      title="Geoff Elliott"
      align="right"
    />
  ));

