/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { Button } from 'react-bootstrap';

import * as C from '../index';

storiesOf('Card', module)
  .add('with header text', () => (
    <C.Card header="Header Text" />
  ))
  .add('with header and body', () => (
    <C.Card header="Header Text">
      <p>You can collapse this card by clicking anywhere in the header.</p>
      <Button><C.Icon icon="alcohol" />Prohibited</Button>
    </C.Card>
  ))
  .add('collapsible', () => (
    <C.Card header="Header Text" footer="Footer Text that stays when collapsed" collapsible>
      <p>You can collapse this card by clicking anywhere in the header.</p>
      <Button><C.Icon icon="alcohol" />Prohibited</Button>
    </C.Card>
  ))
  .add('with simple footer', () => (
    <C.Card footer="Footer Content">
      <p>There is no header, but there is still content on this card.</p>
      <Button><C.Icon icon="alcohol" />Prohibited</Button>
    </C.Card>
  ))
  .add('with media block and custom footer', () => {
    const customFooter = (
      <div>
        <Button bsStyle="success">Approve</Button>
        <Button bsStyle="danger">Deny</Button>
      </div>
    );
    return (
      <C.Card footer={customFooter}>
        <C.CardMedia backgroundColorClass="mercury"><h1>Approval Content</h1></C.CardMedia>
        <p>Content on this card underneath the media block.</p>
      </C.Card>
    );
  })
  .add('with custom content', () => (
    <C.Card style={{ width: '250px' }}>
      <div className="text-center" style={{ margin: '-8px -15px', padding: '8px 15px', background: 'linear-gradient(to bottom, white, white 28px, #d9d9d9 28px, #d9d9d9 29px, white 29px)' }}>
        <C.Avatar className="sapphire" style={{ boxShadow: '0 0 0 2px white' }}><C.Icon icon="badge" /></C.Avatar>
        <div><strong className="text-sapphire">Badge</strong></div>
        <p>Request a badge for a visitor or non-staff member, or get a replacement for yourself.</p>
        <Button bsStyle="primary" style={{ width: '100%' }}>New Badge</Button>
      </div>
    </C.Card>
  ))
  .add('with media block', () => (
    <C.Card>
      <C.CardMedia><h1>Media Heading</h1></C.CardMedia>
      <p>Content on this card underneath the media block.</p>
    </C.Card>
  ))
  .add('with media block and header', () => (
    <C.Card header="Article Type" collapsible>
      <C.CardMedia><h1>Title of the Article</h1></C.CardMedia>
      <p>Content on this card underneath the media block.</p>
    </C.Card>
  ))
  .add('with media block and custom header', () => {
    const customHeader = (
      <C.AvatarInfo title="Geoff Elliott" subTitle="Engineer" />
    );
    return (
      <C.Card header={customHeader}>
        <C.CardMedia backgroundColorClass="mercury"><h1>Title of the Article</h1></C.CardMedia>
        <p>Content on this card underneath the media block.</p>
      </C.Card>
    );
  })
  .add('with image in media block', () => (
    <C.Card style={{ width: '200px' }}>
      <C.CardMedia imageUrl="https://digisource.pnl.gov/im/Devaraj32/Devaraj3.jpg" />
      <p>Content on this card underneath the media block.</p>
    </C.Card>
  ))
  .add('with tint in media block', () => (
    <C.Card style={{ width: '200px' }}>
      <C.CardMedia imageUrl="https://cdn.pnl.gov/images/locations/1326.png" tint="mercury">
        <h1>ISB 2</h1>
      </C.CardMedia>
      <p>Content on this card underneath the media block.</p>
    </C.Card>
  ))
  .add('with lots of content in media block', () => (
    <C.Card style={{ width: '200px' }}>
      <C.CardMedia imageUrl="https://digisource.pnl.gov/im/ISB20/ISB2.jpg" tint="onyx">
        <div className="text-center">
          <C.Avatar src="https://cdn.pnl.gov/images/locations/1326.png" size="lg" />
          <h3>ISB 2</h3>
        </div>
      </C.CardMedia>
      <p>Content on this card underneath the media block.</p>
    </C.Card>
  ));
