/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { action } from '@storybook/addon-actions'; // eslint-disable-line

import { Facility } from '..';

const exampleFacility = {
  id: '3350',
  name: 'Laboratory Support Building',
  abbreviation: 'LSB',
  thumbnailImage: 'https://cdn.pnl.gov/images/locations/1130-thumb.png',
};

storiesOf('Facility', module)
  .add('aligned left', () => (
    <Facility facility={exampleFacility} onClick={action('clicked facility')} />
  ))
  .add('aligned right', () => (
    <Facility facility={exampleFacility} align="right" onClick={action('clicked facility')} />
  ))
  .add('center aligned', () => (
    <Facility facility={exampleFacility} align="center" onClick={action('clicked facility')} />
  ))
  .add('with facility id', () => (
    <Facility id="3350" onClick={action('clicked facility')} />
  ))
  .add('with facility id, no onclick', () => (
    <Facility id="3350" />
  ))
  .add('no thumbnail', () => (
    <Facility facility={{ ...exampleFacility, thumbnailImage: undefined }} />
  ));
