import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line

import * as C from '../index';

storiesOf('Icon', module)
  .add('with file icon default', () => (
    <span style={{ fontSize: '3em' }}>
      <C.Icon icon="file-generic" />
    </span>
  ))
  .add('with file icon, solid', () => (
    <span style={{ fontSize: '3em' }}>
      <C.Icon icon="file-generic" iconDisplay="solid" />
    </span>
  ))
  .add('with file icon, badges', () => (
    <span style={{ fontSize: '3em' }}>
      <C.Icon icon="file-generic" badge="add" />
      <C.Icon icon="file-generic" badge="minus" />
      <C.Icon icon="file-generic" badge="warning" />
      <C.Icon icon="file-generic" badge="error" />
      <C.Icon icon="file-generic" badge="help" />
      <C.Icon icon="file-generic" badge="checkmark" />
    </span>
  ))
  .add('all paths, custom style', () => (
    <div>
      <style dangerouslySetInnerHTML={{
        __html: `
          .pnnl-icon-file-generic-solid-1 {
            fill: transparent !important;
          }
          .pnnl-icon-file-generic-solid-2 {
            fill: #def !important;
          }
          .pnnl-icon-file-generic-default-outline-1 {
            fill: #acf !important;
          }
          .pnnl-icon-file-generic-default-outline-2 {
            fill: #acf !important;
          }
          .pnnl-icon-add-circle {
            fill: #0a0 !important;
          }
          .pnnl-icon-add-mark {
            fill: #fff !important;
          }
        `
      }}
      />
      <span style={{ fontSize: '3em' }}>
        <C.Icon icon="file-generic" iconDisplay="all" badge="add" />
      </span>
    </div>
  ))
