/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { action } from '@storybook/addon-actions'; // eslint-disable-line

import { Location } from '..';

const exampleLocation = {
  id: '1130',
  isFacility: true,
  name: 'Laboratory Support Building',
  thumbnailImage: 'https://cdn.pnl.gov/images/locations/1130-thumb.png',
  facility: {
    id: '3350',
    name: 'Laboratory Support Building',
  }
};

storiesOf('Location', module)
  .add('aligned left', () => (
    <Location location={exampleLocation} onClick={action('clicked location')} />
  ))
  .add('aligned right', () => (
    <Location location={exampleLocation} align="right" onClick={action('clicked location')} />
  ))
  .add('center aligned', () => (
    <Location location={exampleLocation} align="center" onClick={action('clicked location')} />
  ))
  .add('with location (space) id', () => (
    <Location id="5699" onClick={action('clicked location')} />
  ))
  .add('with location (facility) id', () => (
    <Location id="1130" onClick={action('clicked location')} />
  ))
  .add('with location id, no onclick', () => (
    <Location id="5699" />
  ))
  .add('no thumbnail', () => (
    <Location location={{ ...exampleLocation, thumbnailImage: undefined }} />
  ));
