/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { text } from '@storybook/addon-knobs/react'; // eslint-disable-line
import { Col } from 'react-bootstrap';


import * as C from '../index';

storiesOf('NavBar', module)

  .add('single item group', () => (
    <nav className="PnnlNavBar">
      <div id="nav-actions" className="container">
        <C.NavBar>
          <Col sm={12} componentClass="ul">
            <C.NavItem icon="list" text={text('Item 1 Label', 'Dashboard')} />
            <C.NavItem icon="alert" text={text('Item 2 Label', 'System Messages')} />
            <C.NavItem icon="thumbs-up" text={text('Item 3 Label', 'Feedback')} />
          </Col>
        </C.NavBar>
      </div>
    </nav>
  ))
  .add('two item groups', () => (
    <nav className="PnnlNavBar">
      <div id="nav-actions" className="container">
        <C.NavBar>
          <Col sm={6} componentClass="ul">
            <C.NavItem icon="list" text={text('Item 1 Label', 'Dashboard')} />
            <C.NavItem icon="alert" text={text('Item 2 Label', 'System Messages')} />
            <C.NavItem icon="thumbs-up" text={text('Item 3 Label', 'Feedback')} />
          </Col>
          <Col sm={6} componentClass="ul">
            <C.NavItem icon="gear" text="Settings" />
            <C.NavItem icon="help" text="Help" />
          </Col>
        </C.NavBar>
      </div>
    </nav>
  ))
  .add('three item groups', () => (
    <nav className="PnnlNavBar">
      <div id="nav-actions" className="container">
        <C.NavBar>
          <Col sm={3} componentClass="ul">
            <C.NavItem icon="plus-circle" text="New Item" to="/new" />
          </Col>
          <Col sm={6} componentClass="ul">
            <C.NavItem icon="list" text={text('Item 1 Label', 'Dashboard')} to="/dashboard" />
            <C.NavItem icon="alert" text={text('Item 2 Label', 'System Messages')} to="/alert" />
            <C.NavItem icon="thumbs-up" text={text('Item 3 Label', 'Feedback')} to="/feedback" />
          </Col>
          <Col sm={3} componentClass="ul">
            <C.NavItem icon="gear" text="Settings" to="/settings" />
            <C.NavItem icon="help" text="Help" to="/help" />
          </Col>
        </C.NavBar>
      </div>
    </nav>
  ));
