import React from 'react';
import Badge from 'react-bootstrap/lib/Badge';
import PropTypes from 'prop-types';
import Action from '../Action';


const NavItem = ({ count, ...rest }) => (
  <li className="PnnlNavItem">
    <Action iconDisplay="all" {...rest} />
    {count !== null &&
      <Badge>{count}</Badge>
    }
  </li>
);

NavItem.propTypes = {
  /**
   * The name of the icon to be displayed in the NavItem.
   * @required
   */
  icon: PropTypes.string.isRequired,

  /**
   * The label to be displayed underneath the icon in the NavItem.
   * @required
   */
  text: PropTypes.string.isRequired,

  /**
   * The URL/route that the NavItem will link to.
   * @required
   */
  to: PropTypes.string.isRequired,

  /**
   * Determines whether the NavItem will display as 'active' when the 'to' path matches the loaded path exactly, or whether it matches any sub-path.  Example: when 'false', the NavItem will display as 'active' for both actions/ and action/submit. By default this is true. Talk to Geoff Elliott before changing this.
   * @private
   */
  onlyActiveOnIndex: PropTypes.bool,

  /**
   * A number to be displayed as a Badge on the NavItem. Should be used to notify or remind users of items they need to address. This should be a number, not a string or icon.
   */
  count: PropTypes.number,
};

NavItem.defaultProps = {
  onlyActiveOnIndex: true,
  count: null,
};

export default NavItem;
