import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { Col, MenuItem } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';


import * as C from '../index';

storiesOf('NavMenu', module)

  .add('with menu items', () => (
    <nav className="PnnlNavBar">
      <div id="nav-actions" className="container">
        <C.NavBar>
          <Col sm={4} componentClass="ul">
            <C.NavMenu icon="plus-circle" text="New">
              <LinkContainer to="/dashboard">
                <MenuItem>Dashboard</MenuItem>
              </LinkContainer>
              <LinkContainer to="messages/">
                <MenuItem>System Messages</MenuItem>
              </LinkContainer>
              <LinkContainer to="feedback">
                <MenuItem>Feedback</MenuItem>
              </LinkContainer>
            </C.NavMenu>
          </Col>
          <Col sm={4} componentClass="ul">
            <C.NavMenu icon="tools" text="Actions">
              <LinkContainer to="/dashboard">
                <MenuItem>Dashboard</MenuItem>
              </LinkContainer>
              <LinkContainer to="messages/">
                <MenuItem>System Messages</MenuItem>
              </LinkContainer>
              <LinkContainer to="feedback">
                <MenuItem>Feedback</MenuItem>
              </LinkContainer>
            </C.NavMenu>
          </Col>
          <Col sm={4} componentClass="ul">
            <C.NavMenu icon="disguise" text="Sys Admin">
              <LinkContainer to="/dashboard">
                <MenuItem>Dashboard</MenuItem>
              </LinkContainer>
              <LinkContainer to="messages/">
                <MenuItem>System Messages</MenuItem>
              </LinkContainer>
              <LinkContainer to="feedback">
                <MenuItem>Feedback</MenuItem>
              </LinkContainer>
            </C.NavMenu>
          </Col>
        </C.NavBar>
      </div>
    </nav>
  ));
