import React from 'react';
import PropTypes from 'prop-types';
import Grid from 'react-bootstrap/lib/Grid';
import classNames from 'classnames';

const PageHeader = ({
  children, fluid, className, fixed
}) => {
  const headerClasses = classNames(
    className,
    'PnnlPageHeader',
    { fixed },
  );

  return (
    <div className={headerClasses}>
      <Grid fluid={fluid}>
        {children}
      </Grid>
    </div>
  );
};

PageHeader.propTypes = {
  /**
     * The items to display in the PageHeader.  Should include one or more `<Row />` components.
     */
  children: PropTypes.oneOfType([
    PropTypes.arrayOf(PropTypes.node),
    PropTypes.node,
  ]).isRequired,
  /**
     * An additional CSS class to be added to the root PageHeader element.
     */
  className: PropTypes.string,
  /**
     * Determines whether the Container is fluid or fixed-width.
     */
  fluid: PropTypes.bool,
  /**
   * Determines whether the PageHeader is fixed, or scrolls with the page.
   */
  fixed: PropTypes.bool,
};


PageHeader.defaultProps = {
  className: '',
  fluid: false,
  fixed: false,
};

export default PageHeader;
