/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { action } from '@storybook/addon-actions'; // eslint-disable-line
import { Col, Row, Nav, NavDropdown, MenuItem, FormGroup, FormControl, HelpBlock } from 'react-bootstrap';


import * as C from '../index';

storiesOf('PageHeader', module)

  .add('default', () => (
    <C.PageHeader>
      <h1>Page Header</h1>
    </C.PageHeader>
  ))
  .add('fluid, with content', () => (
    <C.PageHeader className="amethyst-10" fluid>
      <Row>
        <Col sm={6}>
          <header>
            <C.Avatar className="amethyst" size="md">
              <C.Icon icon="contract" />
            </C.Avatar>
            <div>
              <small>Purchase Order #12345</small>
              <h1>
                Page Header
              </h1>
            </div>
          </header>
        </Col>
        <Col sm={4}>
          <C.Progress activeStep={1}>
            <C.ProgressStep key="1" onClick={action('clicked step 1')} label="Testing 1" />
            <C.ProgressStep key="2" onClick={action('clicked step 2')} label="Testing 2" />
            <C.ProgressStep key="3" onClick={action('clicked step 3')} label="Testing 3" />
            <C.ProgressStep key="4" onClick={action('clicked step 4')} label="Testing 4" />
          </C.Progress>
        </Col>
        <Col sm={2}>
          <C.ProfilePopover id="0008242" align="center" size="sm" placement="bottom" />
        </Col>
      </Row>
    </C.PageHeader>
  ))
  .add('with className and PageTitle', () => (
    <C.PageHeader className="platinum-30">
      <Row>
        <Col sm={6}>
          <FormGroup controlId="formLargeText" className="PnnlPageTitle">
            <FormControl bsSize="lg" placeholder="Enter text" aria-label="Page Title" />
          </FormGroup>
        </Col>
        <Col sm={4}>
          <C.Progress activeStep={1}>
            <C.ProgressStep key="1" onClick={action('clicked step 1')} label="Testing 1" />
            <C.ProgressStep key="2" onClick={action('clicked step 2')} label="Testing 2" />
            <C.ProgressStep key="3" onClick={action('clicked step 3')} label="Testing 3" />
            <C.ProgressStep key="4" onClick={action('clicked step 4')} label="Testing 4" />
          </C.Progress>
        </Col>
        <Col sm={2}>
          <C.AvatarInfo title="Geoff Elliott" subTitle="Engineer" align="center" size="sm" />
        </Col>
      </Row>
    </C.PageHeader>
  ))
  .add('with className, content and tabs', () => (
    <C.PageHeader className="amethyst invert">
      <Row>
        <Col sm={6}>
          <FormGroup controlId="formLargeText" className="PnnlPageTitle invert">
            <FormControl bsSize="lg" placeholder="Enter text" aria-label="Page Title" />
            <HelpBlock />
          </FormGroup>
        </Col>
        <Col sm={4}>
          <C.Progress activeStep={1} invert>
            <C.ProgressStep key="1" onClick={action('clicked step 1')} label="Testing 1" />
            <C.ProgressStep key="2" onClick={action('clicked step 2')} label="Testing 2" />
            <C.ProgressStep key="3" onClick={action('clicked step 3')} label="Testing 3" />
            <C.ProgressStep key="4" onClick={action('clicked step 4')} label="Testing 4" />
          </C.Progress>
        </Col>
        <Col sm={2}>
          <C.AvatarInfo title="Geoff Elliott" subTitle="Engineer" align="center" size="sm" />
        </Col>
      </Row>
      <Row>
        <Col sm={12}>
          <Nav bsStyle="tabs" activeKey="1">
            <C.Tab to="/details" validationState="error" active>Details</C.Tab>
            <C.Tab to="/procedure">Procedure</C.Tab>
            <C.Tab to="/hazards"><C.Icon icon="fire" /> Hazards</C.Tab>
            <C.Tab to="/spaces" validationState="warning">Spaces</C.Tab>
            <C.Tab to="/training-controlls/">Training &amp; Controls</C.Tab>
            <NavDropdown eventKey="6" title="Dropdown" id="nav-dropdown">
              <MenuItem eventKey="6.1">Action</MenuItem>
              <MenuItem eventKey="6.2">Another action</MenuItem>
              <MenuItem eventKey="6.3">Something else here</MenuItem>
              <MenuItem divider />
              <MenuItem eventKey="6.4">Separated link</MenuItem>
            </NavDropdown>
          </Nav>
        </Col>
      </Row>
    </C.PageHeader>
  ));
