/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { action } from '@storybook/addon-actions'; // eslint-disable-line

import Chip from '../Chip';
import * as C from '../index';

storiesOf('Person', module)
  .add('with example person', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: 'Chief Information Officer',
    };
    return (
      <C.Person person={examplePerson} onClickCallback={action('clicked person')} />
    );
  })
  .add('aligned right', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: 'Chief Information Officer',
    };
    return (
      <C.Person person={examplePerson} align="right" onClickCallback={action('clicked person')} />
    );
  })
  .add('center aligned', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: 'Chief Information Officer',
    };
    return (
      <C.Person person={examplePerson} align="center" onClickCallback={action('clicked person')} />
    );
  })
  .add('center aligned, no title', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: '',
    };
    return (
      <C.Person person={examplePerson} align="center" onClickCallback={action('clicked person')} />
    );
  })
  .add('swapped info', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: 'Chief Information Officer',
    };
    return (
      <C.Person person={examplePerson} onClickCallback={action('clicked person')} isSwapped />
    );
  })
  .add('custom title', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: 'Chief Information Officer',
    };
    return (
      <C.Person person={examplePerson} altTitle="ALD" altName="Walla Walla Guy" onClickCallback={action('clicked person')} />
    );
  })
  .add('deprecated condensed', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: 'Chief Information Officer',
    };
    return (
      <C.Person person={examplePerson} onClickCallback={action('clicked person')} isCondensed />
    );
  })
  .add('element for altName', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson',
      title: 'Chief Information Officer'
    };
    return (
      <C.Person person={examplePerson} altName={<Chip label="Active" color="emerald" size="sm" />} />
    );
  })
  .add('element for altTitle', () => {
    const examplePerson = {
      portraitLink: 'https://api.pnl.gov/operations/v2/portraits/8957518?type=image',
      firstName: 'Brian',
      lastName: 'Abrahamson'
    };
    return (
      <C.Person person={examplePerson} altTitle={<span><Chip label="Active" color="emerald" size="sm" /> Chief Information Officer</span>} />
    );
  });
