/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line

import Chip from '../Chip';
import * as C from '../index';


storiesOf('ProfilePopover', module)
  .add('with default options', () => (
    <C.ProfilePopover id="8845258" />
  ))
  .add('centered, with no title', () => (
    <C.ProfilePopover id="8845258" align="center" altTitle="" />
  ))
  .add('with alt title', () => (
    <C.ProfilePopover id="8845258" altTitle="Awesome Person" />
  ))
  .add('non-PNNL person', () => {
    const examplePerson = {
      id: '12345',
      firstName: 'Jane',
      lastName: 'Smith',
      title: 'University of Washington',
      phoneNumber: '509/375-5555',
      emailAddress: 'jane.smith@contractor.com',
    };

    return (
      <C.ProfilePopover person={examplePerson} />
    );
  })
  .add('alt title with element', () => (
    <C.ProfilePopover id="8845258" altTitle={<span><Chip label="Active" color="emerald" size="sm" /> Engineer</span>} />
  ))
  .add('alt name with element', () => (
    <C.ProfilePopover id="8845258" altName={<Chip label="Inactive" color="ruby" size="sm" />} />
  ));
