import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { action } from '@storybook/addon-actions'; // eslint-disable-line


import * as C from '../index';

storiesOf('SidebarPanel', module)

  .add('with simple heading', () => (
    <C.Sidebar>
      <C.SidebarPanel header="Simple Title">
        <C.SidebarItem title="Item 1" href="http://labweb.pnl.gov" />
        <C.SidebarItem title="Item 2" itemActive count={1} onClick={action('clicked item 2')} />
        <C.SidebarItem title="Item 3" onClick={action('clicked item 3')} />
      </C.SidebarPanel>
    </C.Sidebar>
  ))
  .add('with collapsible panels', () => (
    <C.Sidebar>
      <C.SidebarPanel header="Toggle 1" collapsible>
        <C.SidebarItem title="Item 1" href="http://labweb.pnl.gov" />
        <C.SidebarItem title="Item 2" itemActive count={1} onClick={action('clicked item 2')} />
        <C.SidebarItem title="Item 3" onClick={action('clicked item 3')} />
      </C.SidebarPanel>
      <C.SidebarPanel header="Toggle 2" collapsible>
        <C.SidebarItem title="Item 4" href="http://labweb.pnl.gov" />
        <C.SidebarItem title="Item 5" count={2} onClick={action('clicked item 5')} />
        <C.SidebarItem title="Item 6" onClick={action('clicked item 6')} />
      </C.SidebarPanel>
    </C.Sidebar>
  ))
  .add('with validation', () => (
    <C.Sidebar>
      <C.SidebarPanel header="Toggle 1" validationState="success" collapsible>
        <C.SidebarItem title="Item 1" href="http://labweb.pnl.gov" validationState="success" />
        <C.SidebarItem title="Item 2" itemActive count={1} validationState="success" onClick={action('clicked item 2')} />
        <C.SidebarItem title="Item 3" validatioStaten="success" onClick={action('clicked item 3')} />
      </C.SidebarPanel>
      <C.SidebarPanel header="Toggle 2" validationState="error" collapsible>
        <C.SidebarItem title="Item 4" href="http://labweb.pnl.gov" validationState="incomplete" />
        <C.SidebarItem title="Item 5" validationState="warning" count={2} onClick={action('clicked item 5')} />
        <C.SidebarItem title="Item 6" validationState="error" onClick={action('clicked item 6')} />
      </C.SidebarPanel>
    </C.Sidebar>
  ));
