/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { action } from '@storybook/addon-actions'; // eslint-disable-line


import * as C from '../index';

storiesOf('SidebarWrapper', module)

  .add('collapsible sidebar', () => {
    const sidebar = (
      <C.Sidebar>
        <C.SidebarItem title="Item 1" href="http://labweb.pnl.gov" />
        <C.SidebarItem title="Item 2" itemActive count={1} onClick={action('clicked item 2')} />
        <C.SidebarItem title="Item 3" onClick={action('clicked item 3')} />
      </C.Sidebar>
    );
    const dummyContent = [];
    for (let i = 0; i < 30; i += 1) {
      dummyContent.push(<p key={i}>Dummy Content</p>);
    }
    return (
      <C.SidebarWrapper sidebarComponent={sidebar} collapsible>
        <h1>Other Content</h1>
        {dummyContent}
      </C.SidebarWrapper>
    );
  })
  .add('collapsible right sidebar', () => {
    const sidebar = (
      <C.Sidebar>
        <C.SidebarItem title="Item 1" href="http://labweb.pnl.gov" />
        <C.SidebarItem title="Item 2" itemActive count={1} onClick={action('clicked item 2')} />
        <C.SidebarItem title="Item 3" onClick={action('clicked item 3')} />
      </C.Sidebar>
    );
    const dummyContent = [];
    for (let i = 0; i < 30; i += 1) {
      dummyContent.push(<p key={i}>Dummy Content</p>);
    }
    return (
      <C.SidebarWrapper sidebarComponent={sidebar} collapsible align="right">
        <h1>Other Content</h1>
        {dummyContent}
      </C.SidebarWrapper>
    );
  });
