import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line
import { action } from '@storybook/addon-actions'; // eslint-disable-line

import { Space } from '../index';

const exampleSpace = {
  id: '335011C34',
  name: '1C34',
  description: '1C34',
  thumbnailImage: 'https://cdn.pnl.gov/images/locations/5699-thumb.png',
  facility: {
    id: '3350',
    abbreviation: 'LSB',
  },
};

storiesOf('Space', module)
  .add('aligned left', () => (
    <Space space={exampleSpace} onClick={action('clicked space')} />
  ))
  .add('aligned right', () => (
    <Space space={exampleSpace} align="right" onClick={action('clicked space')} />
  ))
  .add('center aligned', () => (
    <Space space={exampleSpace} align="center" onClick={action('clicked space')} />
  ))
  .add('with space id (office)', () => (
    <Space id="335011C34" onClick={action('clicked space')} />
  ))
  .add('with space id (lab)', () => (
    <Space id="330022519" onClick={action('clicked space')} />
  ))
  .add('with space id, no onclick', () => (
    <Space id="330022519" />
  ))
  .add('no thumbnail', () => (
    <Space space={{ ...exampleSpace, thumbnailImage: undefined }} />
  ));
