/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line

import * as C from '../index';

storiesOf('Timeline', module)
  .add('single default item', () => (
    <C.Timeline>
      <C.TimelineItem>
        <div>
          <h3>Event Title</h3>
          <div className="timestamp">Sep. 25, 02:00 PM</div>
          <p>A thing happened.</p>
        </div>
      </C.TimelineItem>
    </C.Timeline>
  ))
  .add('in a sidebar with header', () => {
    const sidebarTimeline = (
      <C.Sidebar>
        <C.SidebarPanel header="Timeline">
          <C.Timeline>
            <C.TimelineItem>
              <div>
                <h3>Event Title</h3>
                <div className="timestamp">Sep. 25, 02:00 PM</div>
                <p>A thing happened.</p>
              </div>
            </C.TimelineItem>
            <C.TimelineItem>
              <div>
                <h3>Event Title</h3>
                <div className="timestamp">Sep. 25, 02:00 PM</div>
                <p>A thing happened.</p>
              </div>
            </C.TimelineItem>
            <C.TimelineItem>
              <div>
                <h3>Event Title</h3>
                <div className="timestamp">Sep. 25, 02:00 PM</div>
                <p>A thing happened.</p>
              </div>
            </C.TimelineItem>
          </C.Timeline>
        </C.SidebarPanel>
      </C.Sidebar>
    );

    const dummyContent = [];
    for (let i = 0; i < 15; i += 1) {
      dummyContent.push(<p key={i}>Dummy Content</p>);
    }
    return (
      <C.SidebarWrapper sidebarComponent={sidebarTimeline} toggleLabel="Timeline" align="right" collapsible fluid>
        <h1>Other Content</h1>
        {dummyContent}
      </C.SidebarWrapper>
    );
  })
  .add('multiple items and custom avatars', () => {
    const commentAvatar = <C.Avatar className="onyx-80" size="sm"><C.Icon icon="comment" /></C.Avatar>;
    const userAvatar = <C.Avatar size="sm" src="https://api.pnl.gov/operations/v2/portraits/self?type=image" />;
    const rejectAvatar = <C.Avatar size="sm" className="danger"><C.Icon icon="thumbs-down" /></C.Avatar>;
    return (
      <C.Timeline>
        <C.TimelineItem avatar={commentAvatar}>
          <div>
            <h3>Comment</h3>
            <div className="timestamp">Sep. 26, 08:10 AM</div>
            <p>New timeline is great!</p>
          </div>
        </C.TimelineItem>
        <C.TimelineItem avatar={userAvatar}>
          <div>
            <h3>New User</h3>
            <div className="timestamp">Sep. 27, 11:10 PM</div>
            <p>You were added.</p>
          </div>
        </C.TimelineItem>
        <C.TimelineItem avatar={rejectAvatar}>
          <div>
            <h3>Approval Denied</h3>
            <div className="timestamp">Sep. 29, 03:00 PM</div>
            <p>Rejected!!!</p>
          </div>
        </C.TimelineItem>
      </C.Timeline>
    );
  })
  .add('with custom TimeLineItem className', () => {
    const commentAvatar = <C.Avatar className="onyx-80" size="sm"><C.Icon icon="comment" /></C.Avatar>;
    const userAvatar = <C.Avatar size="sm" src="https://api.pnl.gov/operations/v2/portraits/self?type=image" />;
    const rejectAvatar = <C.Avatar size="sm" className="danger"><C.Icon icon="thumbs-down" /></C.Avatar>;
    return (
      <C.Timeline>
        <C.TimelineItem avatar={commentAvatar}>
          <div>
            <h3>Comment (default timeLineItem styling)</h3>
            <div className="timestamp">Sep. 26, 08:10 AM</div>
            <p>New timeline is great!</p>
          </div>
        </C.TimelineItem>
        <C.TimelineItem avatar={userAvatar} className="bg-info">
          <div>
            <h3>New User (info background)</h3>
            <div className="timestamp">Sep. 27, 11:10 PM</div>
            <p>You were added.</p>
          </div>
        </C.TimelineItem>
        <C.TimelineItem avatar={rejectAvatar} className="bg-danger">
          <div>
            <h3>Approval Denied (danger background)</h3>
            <div className="timestamp">Sep. 29, 03:00 PM</div>
            <p>Rejected!!!</p>
          </div>
        </C.TimelineItem>
      </C.Timeline>
    );
  });
