/* eslint import/no-extraneous-dependencies: ["error", {"devDependencies": true}] */
import 'whatwg-fetch';
import React from 'react';
import { storiesOf } from '@storybook/react'; // eslint-disable-line

import * as C from '../index';

storiesOf('ValidationIcon', module)
  .add('success icon', () => (
    <C.ValidationIcon validationState="success" />
  ))
  .add('errror icon', () => (
    <C.ValidationIcon validationState="error" />
  ))
  .add('warning icon', () => (
    <C.ValidationIcon validationState="warning" />
  ))
  .add('incomplete icon', () => (
    <C.ValidationIcon validationState="incomplete" />
  ));
