function getDepartmentColor(departmentCode) {
  if (departmentCode === null) {
    return 'copper';
  }
  const departmentCodeFragment = departmentCode.slice(0, 2);
  switch (departmentCodeFragment) {
    case 'PW':
      return 'emsl-green';
    case 'PE':
      return 'emerald';
    case 'PF':
      return 'sapphire';
    case 'PN':
      return 'ruby';
    case 'PB':
      return 'topaz';
    case 'PC':
      return 'mercury';
    case 'PH':
      return 'garnet';
    case 'PS':
      return 'amethyst';
    default:
      return 'copper';
  }
}

export default getDepartmentColor;
