import React from 'react';
import Col from 'react-bootstrap/es/Col';
import NavBar from 'pnnl-react-core/lib/NavBar';
import NavItem from 'pnnl-react-core/lib/NavItem';

const Navigation = () => (
  <NavBar>
    <Col sm={2} componentClass="ul">
      <NavItem to="/new" icon="plus-circle" text="New" />
    </Col>
    <Col sm={8} componentClass="ul">
      <NavItem to="/" icon="home" text="Home" onlyActiveOnIndex />
      <NavItem to="/feedback" icon="thumbs-up" text="Feedback" />
    </Col>
    <Col sm={2} componentClass="ul">
      <NavItem to="/help" icon="help" text="Help" />
    </Col>
  </NavBar>
);

export default Navigation;